import CovidAssessment from './CovidAssessment'
import CovidTable from './CovidTable'
import React from "react";



class Tripinfo extends React.Component {

    state = {
        population: "",
        covidData: []
    }



    openNav() {
        document.getElementById("bottom-Tripinfo").style.height = "40%";
    }

    closeNav() {
        document.getElementById("bottom-Tripinfo").style.height = "0";
    }

    componentDidMount() {
        console.log("THIS IS MOUNT")
        this.getPopulation()
        this.getCovidData()
        this.openNav()
    }

    componentDidUpdate(prevProps){
        console.log("THIS IS UPDATE")
        if(this.props !== prevProps){
            this.getPopulation()
            this.getCovidData()
        }
    }

    getCovidData = () => {
        let config = {
            method: 'GET',
            headers: {
            'Content-Type': 'application/json',
            Accept: 'application/json',
            Authorization: `Bearer ${this.props.token}`
            }
        }
        fetch(`http://localhost:3001/nytdata/${this.props.destinationfips[0].fips}`,config)
        .then(response => response.json())
        .then(response => {
            this.setState({covidData: response.data})
        })
    }


    getPopulation = () => {
        let fips = this.props.destinationfips[0].fips
        let state;
        let county;
        if (fips.length < 5){
            state = "0" + fips.slice(0,1)
            county = fips.slice(-3)
        } else {
            state = fips.slice(0,2)
            county = fips.slice(-3)
        }

        fetch(`https://api.census.gov/data/2019/pep/population?get=COUNTY,DATE_CODE,DATE_DESC,DENSITY,POP,NAME,STATE&DATE_CODE=12&for=county:${county}&in=state:${state}&key=51999fa2f3c7bac02dceb923be5b78f0d135a3d6`)
        .then(resp => resp.json())
        .then(data => { this.setState({
            population: data?.[1]?.[4]
            })
        })
    }


    render() {
        return (
        <div id="bottom-Tripinfo" className="bottom-nav">
                <div className="trip-info">
                    <div className="results-table">
                    <CovidTable population={this.state.population} covidData={this.state.covidData} />
                    </div>
                    <div className="covid-assessment-container">
                    <CovidAssessment population={this.state.population} covidData={this.state.covidData}/>
                    </div>
                </div>
            <button className="closebtn" onClick={this.closeNav}>&times;</button>
        </div>

        )
    }
}

export default Tripinfo;
