import React, { Component } from "react";
import {GoogleMap, useLoadScript, Marker, InfoWindow} from "@react-google-maps/api";
import usePlacesAutocomplete, {getGeocode, getLatLng} from "use-places-autocomplete";
import {Combobox, ComboboxInput, ComboboxPopover, ComboboxList, ComboboxOption} from "@reach/combobox";
import "@reach/combobox/styles.css";
import mapStyles from './MapStyle'
import {Client} from "@googlemaps/google-maps-services-js";
const client = new Client({});
const libraries = ["places"];
const mapContainerStyle = {
  width: "100%",
  height: "80%",
}

const center = {
  lat: 40.7128,
  lng: -74.0060,
}

const options = {
  styles: mapStyles,
  disableDefaultUI: true,
  zoomControl: true,
  streetViewControl: true,
}

function Map() {

    const {isLoaded, loadError} = useLoadScript({
      googleMapsApiKey: process.env.REACT_APP_GOOGLE_KEY,
      libraries,
    });
    // 1540 Broadway, New York, NY, USA

    const onMapClick = React.useCallback((event) => { 
      url = `https://maps.googleapis.com/maps/api/geocode/json?latlng=${event.latLng.lat()},${event.latLng.lng()}&key=${process.env.REACT_APP_GOOGLE_KEY}`;
      fetch(url)
        .then(response => response.json())
        .then(data => {
          console.log(data);
        })

      // let address = 
      setMarkers(() => [{ 
        // address: 
        lat: event.latLng.lat(),
        lng: event.latLng.lng(),
        time: new Date()
      }]);
    }, []);

    const onSearchSelect = React.useCallback((grid, address) => {
      setMarkers(() => [{
        address: address,
        lat: grid.lat,
        lng: grid.lng,
        time: new Date()
      }]);
    }, []);

    const mapRef = React.useRef();
    const onMapLoad = React.useCallback((map) => {
      mapRef.current = map;
    }, []);

    const panTo = React.useCallback(({lat,lng}) => {
      mapRef.current.panTo({lat,lng});
      mapRef.current.setZoom(14);
    }, []);

    const [markers, setMarkers] = React.useState([]);
    const [selected, setSelected] = React.useState([null]);

    const [infoWindowVisible, setInfoWindowVisible] = React.useState(false);

    if(loadError) return "Error loading maps";
    if(!isLoaded) return "Loading Mpas";

    return (
      <div className="map-container">
        <div className="map">
          <h1 id="map-logo">
            Covid Hater{" "}
            <span role="img" aria-label="virus">
              🦠
            </span>
          </h1>
          <Locate panTo={panTo} />
          <Search panTo={panTo} onSearchSelect={onSearchSelect} />
          <GoogleMap 
            mapContainerStyle={mapContainerStyle} 
            zoom={8} center={center} 
            options={options} 
            onClick={onMapClick} 
            onLoad={onMapLoad}
          >
            {markers.map((marker) => (
              <Marker 
                key={marker.time.toISOString()} 
                position={{lat: marker.lat, lng: marker.lng}}
                icon={{
                  url:"https://static.thenounproject.com/png/113403-200.png",
                  scaledSize: new window.google.maps.Size(50,50),
                  origin: new window.google.maps.Point(0,0),
                  anchor: new window.google.maps.Point(25,25),
                }}
                onClick={() => {
                  setSelected(marker)
                  setInfoWindowVisible(!infoWindowVisible)
                  // console.log(marker.address)
                }}
              >
              </Marker>
            ))}

            {infoWindowVisible && (
              <InfoWindow position={{lat: selected.lat, lng: selected.lng}}>
                <div>
                  <h2>Selected!</h2>
                  <p>Address</p>
                  <p>Latitude: {selected.lat} Longitude:{selected.lng}</p>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </div>
      </div>
    );
}

export default Map;

function Locate({ panTo }) {
  return (
    <button className="locate" onClick={() => {
      navigator.geolocation.getCurrentPosition((position) => {
        panTo({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      }, 
      () => null);
    }}>
      <h1 className="compass">
        <span role="img">
          🧭 
        </span>
      </h1>
    </button>
  )
}

function Search({ panTo, onSearchSelect }) {

  const {
    value, 
    suggestions:{status, data}, 
    setValue, 
    clearSuggestions,
  } = usePlacesAutocomplete({
    requestOptions: {
      location: {lat: () => 40.7128, lng: () => -74.0060},
      radius: 200 * 1000
    }
  })

  return (
    <div className="search-bar">
      <Combobox 
        onSelect={async (address) => {
          setValue(address, false);
          clearSuggestions()
          try {
            const results = await getGeocode({address});
            const { lat, lng } = await getLatLng(results[0]);
            panTo({ lat, lng });
            {onSearchSelect({ lat, lng }, address)}

          } catch(error){
            console.log("Error!")
          }
        }}
      >
        <ComboboxInput 
          value={value} 
          onChange={(e) => {
            setValue(e.target.value)
          }}
          placeholder="Enter a address"
        />
        <ComboboxPopover>
          <ComboboxList>
            {status === "OK" && 
              data.map(({place_id, description}) => (
                <ComboboxOption key={place_id} value={description}/>
              ))}
          </ComboboxList>
        </ComboboxPopover>
      </Combobox>
    </div>
  )
}
