import * as React from 'react';
import {Skeleton} from 'baseui/skeleton';


 /*The hot spots map shows the share of population with a new reported case over the last week. 
 Parts of a county with a population density lower than 10 people per square mile are not shaded. 
 Data for Rhode Island is shown at the state level because county level data is infrequently reported. 
 For total cases and deaths: The map shows the known locations of coronavirus cases by county. 
 Circles are sized by the number of people there who have tested positive or have a probable case of the virus, which may differ from where they contracted the illness. 
 For per capita: Parts of a county with a population density lower than 10 people per square mile are not shaded.   

 Risk factors:
Chances of contraction
    population density per cases  = Density of active cases per density of population'

Changes of death
    Your age per deaths in this area by the number of deaths in the US ?
    Your Age + co-morbidity + contraction %

Heatmap

DAILY CASES
 #of cases daily 
    > 400 Severe Level 1: Severe Uncontrolled
Community Transmission
    201 - 400 moderate  Level 2: Significant Uncontrolled
Community Transmission

101-200
Level 3: Moderate,
Controlled Transmission

Level 4: Minimal,
Controlled Transmission
<100 

    Density of cases per population

    day 8 - day 7 = fist num
     day 7 - day 6 = second num 
     day 6 - day 5 = thir d etx 
     get average of nums / 7 = ave cases daily

     if daily cases are > x do some colorize on heatmap component. 

        population(x) / 100000  = popscale
        #deaths/#cases = 
        on day 2/3/4 what is the (cases average / average deaths)


 */


export default (props) => {
const {covidData, population} = props;

const getLinks = () => {
    fetch(`https://api.covidtracking.com/v1/states/tx/info.json`)
    .then(response => response.json())
    .then(response => {
        return <>
                    <div>
                        <ul>
                            <li><a href={response.covid19Site}>Main Covid info</a></li>
                            <li><a href={response.covid19SiteSecondary}>Secondary Info</a></li>
                            <li><a href={response.covid19SiteTertiary}>Tertiary Info</a></li>
                        </ul>
                    </div>
                </>
    })
}

    if (!covidData?.length || !population?.length) return null;
    // if (!covidData || !covidData.length )
    // if (!covidData.length) //covidData null undefined

    const casesDeltas = covidData.reduce((total, currentValue, currentIndex) => {
        if (currentIndex === 0 || currentIndex > covidData.length - 2) return total; // skip if the first day
        const prevValue = covidData?.[currentIndex-1];
        const delta = (currentValue?.[0]?.cases || 0) - (prevValue?.[0]?.cases || 0);
        return [...total, delta];
    }, []);

    const deathsDeltas = covidData.reduce((total, currentValue, currentIndex) => {
        if (currentIndex === 0 || currentIndex > covidData.length - 2) return total; // skip if the first day
        const prevValue = covidData?.[currentIndex-1];
        const delta = (currentValue?.[0]?.deaths || 0) - (prevValue?.[0]?.deaths || 0);
        return [...total, delta];
    }, [])

   const riskAssess = (yestCases) => {
        if(yestCases > 400){
            props.getRiskAssessment("Risk: Severe Uncontrolled Community Transmission")
            return  <>
                        <div className="risk-card" style={{backgroundColor: "red"}}>

                            <b>Risk: Severe Uncontrolled Community Transmission</b>
                        </div>
                        <div className="daily-life">
                        <h3><b><u>Daily Life</u></b></h3>
                        <ol>
                            <li>Stay home, except for essential activities.</li>
                            <li>Avoid and cancel all gatherings of any size</li>
                            <li>"All vulnerable individuals (65+ orwith pre-existing health conditions) stay home when possible."</li>
                            <li>Self-quarantine responsibly for 14 days if in close and prolonged contact with someone who has COVID-19</li>
                            <li>Wear face coverings to protect others.</li>
                            <li> Essential workers practice special precautions to prevent spread.</li>
                            <li>Avoid non-essential business and personal travel. Follow state guidance for quarantine after visiting certain locations.</li>
                            <li> Avoid public transportation where possible.</li>
                        </ol>
                        </div>
                    </>

       } else if(yestCases > 201 && yestCases < 400 ){
            props.getRiskAssessment("Risk: Severe Uncontrolled Community Transmission")
            return  <>
                        <div className="risk-card" style={{backgroundColor: "Orange"}}>\
                            <b>Risk: Significant Uncontrolled Community Transmission</b>
                        </div>
                    </>

       } else if(yestCases > 101 && yestCases < 200 ){
            props.getRiskAssessment("Risk: Moderate, Uncontrolled Community Transmission")
            return  <>
                        <div className="risk-card" style={{backgroundColor: "Yellow"}}>\
                            <b>Risk: Moderate, Uncontrolled Community Transmission</b>
                        </div>
                    </>

       } else if(yestCases < 101){
            props.getRiskAssessment("Risk: Minimal, Controlled Transmission")
            return  <>
                        <div className="risk-card" style={{backgroundColor: "Green"}}> \
                            <b>Risk: Minimal, Controlled Transmission</b>
                        </div>
                    </>
       }
   }

  return (
  <>
    <div>{riskAssess(casesDeltas?.[13])}</div>
    <div>New Cases Yesterday: {casesDeltas?.[13]}</div>
    <div>New Deaths Yesterday: {deathsDeltas?.[13]} </div>
    <div>Withing the last two weeks<br/> there were: {covidData[14][0]?.cases - covidData[1][0]?.cases} cases</div>
    {getLinks()}
  </>
  );
};


// https://api.covidtracking.com/v1/states/tx/info.json use this data
