    const [selected, setSelected] = React.useState([null]);
    const [origin, setOrigin] = React.useState([]);
    const [destination, setDestination] = React.useState([]);
    const [path, setPath] = React.useState([]);

    //error handling for path coordinates
    const [infoWindowVisible, setInfoWindowVisible] = React.useState(false);
    if(loadError) return "Error loading maps";
    if(!isLoaded) return "Loading Mpas";
    console.log(destinationfips)
    return (
        <div className="map-container">
          <div className="map">

            <h1 id="map-logo">
              Covid Hater{" "}
              <span role="img" aria-label="virus">🦠</span>
            </h1>
            <Locate panTo={panTo} onLocate={onLocate} />
            <SearchOrigin panTo={panTo} selectOrigin={selectOrigin}  />
            <SearchDestination panTo={panTo} selectDestination={selectDestination} />
            <button className="direction-button" onClick={origin && destination ? applyAddress : null} >
              Direction
            </button>
            <GoogleMap 
              mapContainerStyle={mapContainerStyle} 
              zoom={8} center={center} 
              options={options} 
              onClick={onMapClick} 
              onLoad={onMapLoad}
            >
              {markers.map((marker) => (
                <Marker 
                  key={marker.time.toISOString()} 
                  position={{lat: marker.lat, lng: marker.lng}}
                  icon={{
                    url:"https://static.thenounproject.com/png/113403-200.png",
                    scaledSize: new window.google.maps.Size(50,50),
                    origin: new window.google.maps.Point(0,0),
                    anchor: new window.google.maps.Point(25,25),
                  }}
                  onClick={() => {
                    setSelected(marker)
                    setInfoWindowVisible(!infoWindowVisible)
                  }}
                >
                </Marker>
              ))}
              {origin.map((marker) => (
                <Marker 
                  key={marker.time.toISOString()} 
                  position={{lat: marker.lat, lng: marker.lng}}
                  icon={{
                    url:"https://static.thenounproject.com/png/113403-200.png",
                    scaledSize: new window.google.maps.Size(50,50),
                    origin: new window.google.maps.Point(0,0),
                    anchor: new window.google.maps.Point(25,25),
                  }}
                  onClick={() => {
                    setSelected(marker)
                    setInfoWindowVisible(!infoWindowVisible)
                  }}
                >
                </Marker>
              ))}
              {destination.map((marker) => (
                <Marker 
                  key={marker.time.toISOString()} 
                  position={{lat: marker.lat, lng: marker.lng}}
                  icon={{
                    url:"https://static.thenounproject.com/png/113403-200.png",
                    scaledSize: new window.google.maps.Size(50,50),
                    origin: new window.google.maps.Point(0,0),
                    anchor: new window.google.maps.Point(25,25),
                  }}
                  onClick={() => {
                    setSelected(marker)
                    setInfoWindowVisible(!infoWindowVisible)
                  }}
                >
                </Marker>
              ))}
              {path.length > 0 ?
              <Polyline
                  path={path[0].path}
                  geodesic={true}
                  options={{
                      strokeColor: "#ff2527",
                      strokeOpacity: 0.75,
                      strokeWeight: 2,
                  }}
              />
              : null
              }
              {infoWindowVisible && (
                <InfoWindow position={{lat: selected.lat, lng: selected.lng}}>
                  <div>
                    <h2>Selected!</h2>
                    <p>Address: {selected.address}</p>
                    <p>Latitude: {selected.lat} Longitude:{selected.lng}</p>
                  </div>
                </InfoWindow>
              )}
            </GoogleMap>
          </div>
          {destinationfips.length > 0 && path.length > 0 ? <Tripinfo direction={path[0].directionInfo} token={props.token} destinationfips={destinationfips} /> : null}
        </div>
    );
}

export default Map;

function Locate({ panTo, onLocate }) {
  return (
    <button className="locate" onClick={() => {
      navigator.geolocation.getCurrentPosition((position) => {
        panTo({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
        onLocate(position)
      }, 
      () => null);
    }}>
      <h1 className="compass">
        <span role="img" aria-label="compass">🧭 </span>
      </h1>
    </button>
  )
}

function SearchOrigin({ panTo, selectOrigin }) {

  const {
    value, 
    suggestions:{status, data}, 
    setValue, 
    clearSuggestions,
  } = usePlacesAutocomplete({
    requestOptions: {
      location: {lat: () => 40.7128, lng: () => -74.0060},
      radius: 200 * 1000
    }
  })

  return (
      <div className="search-bar1">
        <Combobox 
          onSelect={async (address) => {
            setValue(address, false);
            clearSuggestions()
            try {
              const results = await getGeocode({address});
              const { lat, lng } = await getLatLng(results[0]);
              panTo({ lat, lng });
              {selectOrigin({ lat, lng }, address)}
            } catch(error){
              console.log("Error!")
            }
          }}
        >
          <ComboboxInput 
            value={value} 
            onChange={(e) => {
              setValue(e.target.value)
            }}
            placeholder="Enter a address"
          />
          <ComboboxPopover>
            <ComboboxList>
              {status === "OK" && 
                data.map(({place_id, description}) => (
                  <ComboboxOption key={place_id} value={description}/>
                ))}
            </ComboboxList>
          </ComboboxPopover>
        </Combobox>
      </div>
  )
}

