import React from "react";
import Directions from "./Directions"
import {DirectionsRenderer} from "@react-google-maps/api";

class Tripinfo extends React.Component {

    state = {
        population: "",
        covidData: []
    }

    componentDidMount() {
        this.getPopulation()
        this.getCovidData()
    }

    getCovidData = () => {
        let config = {
            method: 'GET',
            headers: {
            'Content-Type': 'application/json',
            Accept: 'application/json',
            Authorization: `Bearer ${this.props.token}`
            }
        }
        fetch(`http://localhost:3001/nytdata/${this.props.destinationfips[0].fips}`,config)
        .then(response => response.json())
        .then(response => this.setState({
            covidData: response
        }))
    }


    getPopulation = () => {
        let fips = this.props.destinationfips[0].fips
        let state;
        let county;
        if (fips.length < 5){
            state = "0" + fips.slice(0,1)
            county = fips.slice(-3)
        } else {
            state = fips.slice(0,2)
            county = fips.slice(-3)
        }

        fetch(`https://api.census.gov/data/2019/pep/population?get=COUNTY,DATE_CODE,DATE_DESC,DENSITY,POP,NAME,STATE&DATE_CODE=12&for=county:${county}&in=state:${state}&key=51999fa2f3c7bac02dceb923be5b78f0d135a3d6`)
        .then(resp => resp.json())
        .then(data => { this.setState({
            population: data[1][4]
            })
        })
    }

    renderDirections = () => {
        return this.props.direction.steps.map(directionObj => <Directions key={directionObj.encoded_lat_lngs} directionObj={directionObj} />)
    }

    render() {
        return (
            <div className="trip-info">
                <div className="direction-container">
                    <h2>Directions</h2>
                    <p>Distance: {this.props.direction.distance.text}, Travel Time: {this.props.direction.duration.text}</p>
                    <ol>
                        {this.renderDirections()}
                    </ol>
                </div>
                <div className="covid-assessment-container">
                </div>
            </div>
        )
    }
}

export default Tripinfo;
