import React from "react";
import {GoogleMap, useLoadScript, Marker, InfoWindow} from "@react-google-maps/api";
import usePlacesAutocomplete, {getGeocode, getLatLng} from "use-places-autocomplete";
import {Combobox, ComboboxInput, ComboboxPopover, ComboboxList, ComboboxOption} from "@reach/combobox";
import "@reach/combobox/styles.css";
import mapStyles from './MapStyle';
const libraries = ["places"];
const mapContainerStyle = {
  width: "100%",
  height: "80%",
}

const center = {
  lat: 40.7128,
  lng: -74.0060,
}

const options = {
  styles: mapStyles,
  disableDefaultUI: true,
  zoomControl: true,
  streetViewControl: true,
}

function Map() {

    const {isLoaded, loadError} = useLoadScript({
      googleMapsApiKey: process.env.REACT_APP_GOOGLE_KEY,
      libraries,
    });

    //mode input can be driving(default), walking bicycling &mode=driving

    const changeAddressFormat = (startingAddress, EndAddress) => {
      console.log(startingAddress, EndAddress)

    }

    const getDirection = (startingAddress, EndAddress) => {
      const url = `https://maps.googleapis.com/maps/api/directions/json?origin=${startingAddress}&destination=${EndAddress}&key=${process.env.REACT_APP_GOOGLE_KEY}`
      let proxyUrl = 'https://cors-anywhere.herokuapp.com/'
      fetch(proxyUrl + url)
      .then(response => response.json())
      .then(data => {
        // let routeArray = data.routes[0].legs[0].steps
        console.log(data)
      })
    }

    const collectPath = (routeArray) => {
      let collectedArray = []
      for ( let i = 0; i < routeArray.length; i++ ){
        let eachPoint = []
        eachPoint.push(routeArray[i].start_location.lat, routeArray[i].start_location.lng)
        collectedArray.push(eachPoint)
      }
      setPath(() => [{ 
        path: collectedArray.join("|")
      }], []);
    }

    const snapToRoad = () => {
      const url = `https://roads.googleapis.com/v1/snapToRoads?path=${path}&interpolate=true&key=${process.env.REACT_APP_GOOGLE_KEY}`
      fetch( url)
      .then(response => response.json())
      .then(data => console.log(data))
    }

    const getFips = React.useCallback((lat, lng) => {
      const url = `https://geo.fcc.gov/api/census/area?lat=${lat}&lon=${lng}&format=json`
      fetch(url)
        .then(response => response.json())
        .then(data => {
          setFips(() => [{
            fips: data.results[0].county_fips,
            county_name: data.results[0].county_name,
          }]);
      })
    }, []);

    const onMapClick = React.useCallback((event) => {
      getDirection()
      // snapToRoad()
      getFips(event.latLng.lat(), event.latLng.lng())
      const url = `https://maps.googleapis.com/maps/api/geocode/json?latlng=${event.latLng.lat()},${event.latLng.lng()}&key=${process.env.REACT_APP_GOOGLE_KEY}`;
      fetch(url)
        .then(response => response.json())
        .then(data => {
          setMarkers(() => [
            { 
              address: data.results[0].formatted_address,
              lat: event.latLng.lat(),
              lng: event.latLng.lng(),
              time: new Date()
          }]);
      })
    }, []);

    const onLocate = React.useCallback((position) => {
      getFips(position.coords.latitude, position.coords.longitude)
      const url = `https://maps.googleapis.com/maps/api/geocode/json?latlng=${position.coords.latitude},${position.coords.longitude}&key=${process.env.REACT_APP_GOOGLE_KEY}`;
      fetch(url)
      .then(response => response.json())
      .then(data => {
        setMarkers(() => [{ 
          address: data.results[0].formatted_address,
          lat: position.coords.latitude,
          lng: position.coords.longitude,
          time: new Date()
        }]);
      })
    }, []);

    const selectOrigin = React.useCallback((grid, address) => {
      setOrigin(() => [{
        address: address,
        lat: grid.lat,
        lng: grid.lng,
        time: new Date()
      }]);
    }, []);

    const selectDeparture = React.useCallback((grid, address) => {
      setDeparture(() => [{
        address: address,
        lat: grid.lat,
        lng: grid.lng,
        time: new Date()
      }]);
    }, []);

    const mapRef = React.useRef();
    const onMapLoad = React.useCallback((map) => {
      mapRef.current = map;
    }, []);

    const panTo = React.useCallback(({lat,lng}) => {
      mapRef.current.panTo({lat,lng});
      mapRef.current.setZoom(15);
    }, []);

    const [fips, setFips] = React.useState([]);
    const [markers, setMarkers] = React.useState([]);
    const [selected, setSelected] = React.useState([null]);
    const [path, setPath] = React.useState([]);
    const [origin, setOrigin] = React.useState([]);
    const [departure, setDeparture] = React.useState([]);

    const [infoWindowVisible, setInfoWindowVisible] = React.useState(false);

    if(loadError) return "Error loading maps";
    if(!isLoaded) return "Loading Mpas";

    return (
      <div className="map-container">
        <div className="map">
          <h1 id="map-logo">
            Covid Hater{" "}
            <span role="img" aria-label="virus">ðŸ¦ </span>
          </h1>
          <Locate panTo={panTo} onLocate={onLocate} />
          <SearchOrigin panTo={panTo} selectOrigin={selectOrigin}  />
          <SearchDeparture panTo={panTo} selectDeparture={selectDeparture} />
          <button className="direction-button" onClick={origin && departure ? changeAddressFormat(origin, departure) : null} >
            Direction
          </button>
          <GoogleMap 
            mapContainerStyle={mapContainerStyle} 
            zoom={8} center={center} 
            options={options} 
            onClick={onMapClick} 
            onLoad={onMapLoad}
          >
            {!origin && !departure ? markers.map((marker) => (
              <Marker 
                key={marker.time.toISOString()} 
                position={{lat: marker.lat, lng: marker.lng}}
                icon={{
                  url:"https://static.thenounproject.com/png/113403-200.png",
                  scaledSize: new window.google.maps.Size(50,50),
                  origin: new window.google.maps.Point(0,0),
                  anchor: new window.google.maps.Point(25,25),
                }}
                onClick={() => {
                  setSelected(marker)
                  setInfoWindowVisible(!infoWindowVisible)
                }}
              >
              </Marker>
            )) : null}
            {origin.map((marker) => (
              <Marker 
                key={marker.time.toISOString()} 
                position={{lat: marker.lat, lng: marker.lng}}
                icon={{
                  url:"https://static.thenounproject.com/png/113403-200.png",
                  scaledSize: new window.google.maps.Size(50,50),
                  origin: new window.google.maps.Point(0,0),
                  anchor: new window.google.maps.Point(25,25),
                }}
                onClick={() => {
                  setSelected(marker)
                  setInfoWindowVisible(!infoWindowVisible)
                }}
              >
              </Marker>
            ))}
            {departure.map((marker) => (
              <Marker 
                key={marker.time.toISOString()} 
                position={{lat: marker.lat, lng: marker.lng}}
                icon={{
                  url:"https://static.thenounproject.com/png/113403-200.png",
                  scaledSize: new window.google.maps.Size(50,50),
                  origin: new window.google.maps.Point(0,0),
                  anchor: new window.google.maps.Point(25,25),
                }}
                onClick={() => {
                  setSelected(marker)
                  setInfoWindowVisible(!infoWindowVisible)
                }}
              >
              </Marker>
            ))}

            {infoWindowVisible && (
              <InfoWindow position={{lat: selected.lat, lng: selected.lng}}>
                <div>
                  <h2>Selected!</h2>
                  <p>Address: {selected.address}</p>
                  <p>Latitude: {selected.lat} Longitude:{selected.lng}</p>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </div>
      </div>
    );
}

