import React from 'react';

class SaveTripForm extends React.Component {

    postTrip = () => {
        let tripObj = {
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            dob: this.state.dob,
            user_name: this.state.user_name,
            password: this.state.password,
            current_location:this.state.current_location,
            nickname: this.state.nickname

        }
        const options = {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              Accept: 'application/json'
            },
            body: JSON.stringify({user:userObj})
        }

        fetch("http://localhost:3001/users", options)
        .then(response => response.json())
        .then(response => {
            this.setCookie("jwt",response.jwt,1)
            this.props.saveUser(response)
            this.setState({ redirect: true })
        })
    }

    render() {
        return (
            <div id="myModal" className="modal">
                <div className="modal-content">
                    <span className="close" onClick={this.props.toggleModal}>&times;</span>
                    <p>Save Your Trip!</p>
                    <form>
                        <label>
                            Trip Name:
                            <input type="text" name="trip_name" />
                        </label>
                        <p>Starting Point:</p>
                        <h4>{this.props.origin[0]?.address}</h4>
                        <p>End Point:</p>
                        <h4>{this.props.destination[0]?.address}</h4>
                        <input type="submit" value="submit" />
                    </form>
                </div>
            </div>
        )
    }
}

export default SaveTripForm
