import * as React from 'react';
import {useStyletron} from 'baseui';
import {StyledLink} from 'baseui/link';
import {Layer} from 'baseui/layer';
// import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
// import Login from './components/Login'
// import SignUp from './components/SignUp'





import {
  ChevronDown,
  Delete,
  Overflow as UserIcon,
  Upload as Icon,
} from 'baseui/icon';
import {
  Unstable_AppNavBar as AppNavBar,
  POSITION,
} from 'baseui/app-nav-bar';
function renderItem(item) {
  return item.label;
}



const MAIN_NAV = [

  {
    icon: Icon,
    item: {label: 'Travel'},
    mapItemToNode: renderItem,
    mapItemToString: renderItem,
    navExitIcon: Delete,
    navPosition: {desktop: POSITION.horizontal},
    nav: [
      {
        icon: Icon,
        item: {label: 'Create Trip'},
        mapItemToNode: renderItem,
        mapItemToString: renderItem,
      },
      {
        icon: Icon,
        item: {label: 'View Saved Trips'},
        mapItemToNode: renderItem,
        mapItemToString: renderItem,
      },
    ],
  }
];



const USER_NAV = [
  {
    icon: UserIcon,
    item: {label: 'Profile'},
    mapItemToNode: renderItem,
    mapItemToString: renderItem,
  },
  {
    icon: UserIcon,
    item: {label: 'Logout'},
    mapItemToNode: renderItem,
    mapItemToString: renderItem,
  }
];



function isActive(arr, item, activeItem) {
  let active = false;
  for (let i = 0; i < arr.length; i++) {
    const elm = arr[i];
    if (elm === item) {
      if (item === activeItem) return true;
      return isActive(
        (item && item.nav) || [],
        activeItem,
        activeItem,
      );
    } else if (elm.nav) {
      active = isActive(elm.nav || [], item, activeItem);
    }
  }
  return active;
}


export default () => {
  const [css] = useStyletron();
  const [activeNavItem, setActiveNavItem] = React.useState();
  const containerStyles = css({
    boxSizing: 'border-box',
    width: '100vw',
    position: 'fixed',
    top: '0',
    left: '0',
  });


  const appDisplayName = (
    <StyledLink
      $style={{
        textDecoration: 'none',
        color: 'inherit',
        ':hover': {color: 'inherit'},
        ':visited': {color: 'inherit'},
      }}
      href={'#'}
    >
      Covid: Travel Risk Assessment Tool
    </StyledLink>
  );

  return (
    <React.Fragment>
        <Layer>
          <div className={containerStyles}>
            <AppNavBar
              appDisplayName={appDisplayName}
              mainNav={MAIN_NAV}
              isNavItemActive={({item}) => {
                return (
                  item === activeNavItem ||
                  isActive(MAIN_NAV, item, activeNavItem)
                );
              }}
              onNavItemSelect={({item}) => {
                if(item === activeNavItem)
                return;
                setActiveNavItem(item);
              }}
              userNav={USER_NAV}
              username="Duke Ko"
              // if(document.cookieusername)=
              usernameSubtitle="Coolest Programmer"
              userImgUrl=""
            />
          </div>
        </Layer>
      ) : null}
    </React.Fragment>
  );
};
