    const directionsRenderer = new google.maps.DirectionsRenderer();

    const fixInstructionsString = () => {
        console.log(props)
        let doc = new DOMParser().parseFromString(props.directionObj.instructions, "text/html");
        let fixedInstruction = doc.body.innerText.replaceAll("/<wbr>", " ")
        return fixedInstruction.replaceAll("<b>", "").replaceAll("</b>", "").replaceAll('<div style="font-size:0.9em">', "").replaceAll("</div>", "")
    }   
        return (
            <li>
                <p>{fixInstructionsString()}</p>
            </li> 
        );
}
