import React, { Component } from "react";

class Tripinfo extends React.Component {

    state = {
        population: ""
    }

    componentDidMount() {
        this.getPopulation()
        this.getCovidData()
    }

    getPopulation = () => {
        let fips = this.props.destinationfips[0].fips
        let state;
        let county;
        if (fips.length < 5){
            state = "0" + fips.slice(0,1)
            county = fips.slice(-3)
        } else {
            state = fips.slice(0,2)
            county = fips.slice(-3)
        }

        fetch(`https://api.census.gov/data/2019/pep/population?get=COUNTY,DATE_CODE,DATE_DESC,DENSITY,POP,NAME,STATE&DATE_CODE=12&for=county:${county}&in=state:${state}&key=51999fa2f3c7bac02dceb923be5b78f0d135a3d6`)
        .then(resp => resp.json())
        .then(data => { this.setState({
            population: data[1][4]
            }, () => console.log(this.state.population) )
        })
    }

    getCovidData() {
        fetch(`https://localhost:3001/nytdata/${this.props.destinationfips[0].fips}`)
        .then(resp => resp.json())
        .then(data => console.log(data))
    }

    render() {
        return (
            <div className="trip-info">
                <h1>Hello</h1>
            </div>
        )
    }
}

export default Tripinfo;
